import React from 'react'
import './Story.css'

function Story(props) {
  console.log('The props passed to the Story component were: ' + props.id)

  function handleUpVote() {
    props.postVote('up', props.id)
  }

  function handleDownVote() {
    props.postVote('down', props.id)
  }

  function handleDelete() {
    props.deleteStory(props.id)
  }

  const { title, score, url } = props.story

  return (
    <li>
      <button className="upvote" onClick={handleUpVote}>
        ⬆
      </button>
      <button className="downvote" onClick={handleDownVote}>
        ⬇
      </button>
      <a className="title" href={url}>
        {title}
      </a>{' '}
      ({score} points){' '}
      <p className="delete" onClick={handleDelete}>
        ❌
      </p>
    </li>
  )
}

export default Story
