import './App.css'
import React, { useEffect, useState } from 'react'
import Story from './Story'
import AddNewsStory from './AddNewsStory'
import RegistrationPage from './RegistrationPage'

function App(props) {
  const [stories, setStories] = useState([])
  const [loading, setLoading] = useState(false)
  const [displayRegistration, setDisplayRegistration] = useState('none')
  const [displayLogin, setDisplayLogin] = useState('none')
  const [displayMainPage, setDisplayMainPage] = useState('default')

  useEffect(async () => {
    await fetchData()
  }, [])

  async function fetchData() {
    // setLoading(true)
    let response = await fetch('http://localhost:8080/stories')
    let json = await response.json()
    setLoading(false)
    setStories(json)
  }

  async function postVote(direction, id) {
    let directionData = { direction: direction }

    await fetch(`http://localhost:8080/stories/${id}/votes`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(directionData)
    })
    fetchData()
  }

  function handleRegisterClick() {
    // setDisplayMainPage('none')
    setDisplayRegistration('default')
  }

  function handleLoginClick() {
    setDisplayMainPage('none')
    setDisplayLogin('default')
  }

  async function deleteStory(id) {
    await fetch(`http://localhost:8080/stories/${id}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json'
      }
    })
    fetchData()
  }

  function getStoriesComponentList(stories) {
    if (stories.length > 0) {
      return stories.map((story) => (
        <Story
          key={story.title}
          story={story}
          id={story.id}
          postVote={postVote}
          deleteStory={deleteStory}
        />
      ))
    } else {
      return <p>no stories yet</p>
    }
  }

  function getLoadingComponent() {
    return <div className="loader" />
  }

  return (
    <div className="App">
      <div className="registration-page" style={{ display: 'default' }}>
        <RegistrationPage />
      </div>
      <div className="login-page" style={{ display: displayLogin }}>
        login page
      </div>
      <div className="login-page"></div>
      <div className="main-app" style={{ display: displayMainPage }}>
        <header>
          <h1>Very Good™️ Social News Site</h1>
          <div className="user-buttons">
            <button className="user register" onClick={handleRegisterClick}>
              Register
            </button>
            <button className="user login" onClick={handleLoginClick}>
              Login
            </button>
          </div>
        </header>
        <main>
          <h2>Top Stories</h2>
          {loading ? getLoadingComponent() : getStoriesComponentList(stories)}
        </main>
        <AddNewsStory fetchData={fetchData} />
      </div>
    </div>
  )
}

export default App
