import "./App.css";
import React, { useEffect, useState } from "react";
import Story from "./Story";
import AddNewsStory from "./AddNewsStory";
import RegistrationPage from "./RegistrationPage";
import LoginPage from "./LoginPage";

function App(props) {
  const [stories, setStories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [displayRegistration, setDisplayRegistration] = useState(false);
  const [displayLogin, setDisplayLogin] = useState(false);
  const [displayMainPage, setDisplayMainPage] = useState(true);

  useEffect(async () => {
    setLoading(true);
    await fetchData();
    setLoading(false);
  }, []);

  async function fetchData() {
    let response = await fetch("http://localhost:8080/stories");
    let json = await response.json();
    setStories(json);
  }

  async function postVote(direction, id) {
    let directionData = { direction: direction };

    await fetch(`http://localhost:8080/stories/${id}/votes`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(directionData),
    });
    fetchData();
  }

  function handleRegisterClick() {
    setDisplayMainPage(false);
    setDisplayRegistration(true);
  }

  function handleLoginClick() {
    setDisplayMainPage(false);
    setDisplayLogin(true);
  }

  function handleMainPageClick() {
    setDisplayRegistration(false);
    setDisplayLogin(false);
    setDisplayMainPage(true);
  }

  async function deleteStory(id) {
    await fetch(`http://localhost:8080/stories/${id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      },
    });
    fetchData();
  }

  function getStoriesComponentList(stories) {
    if (stories.length > 0) {
      return stories.map((story) => (
        <Story
          key={story.title}
          story={story}
          id={story.id}
          postVote={postVote}
          deleteStory={deleteStory}
        />
      ));
    } else {
      return <p>no stories yet</p>;
    }
  }

  function getLoadingComponent() {
    return <div className="loader" />;
  }

  return (
    <div className="App">
      {displayRegistration ? (
        <div className="registration-page">
          <RegistrationPage handleMainPageClick={handleMainPageClick} />
        </div>
      ) : null}
      {displayLogin ? (
        <div className="login-page">
          <LoginPage handleMainPageClick={handleMainPageClick} />
        </div>
      ) : null}
      {displayMainPage ? (
        <div className="main-app">
          <header>
            <h1>Social News Site</h1>
            <div className="user-buttons">
              <button className="user register" onClick={handleRegisterClick}>
                Register
              </button>
              <button className="user login" onClick={handleLoginClick}>
                Login
              </button>
            </div>
          </header>
          <main>
            <h2>Top Stories</h2>
            {loading ? getLoadingComponent() : getStoriesComponentList(stories)}
          </main>
          <AddNewsStory fetchData={fetchData} />
        </div>
      ) : null}
    </div>
  );
}

export default App;
