import './App.css'
import React, { useEffect, useState } from 'react'
import Story from './Story'

function App(props) {
  const [stories, setStories] = useState([])
  const [loading, setLoading] = useState(false)

  useEffect(async () => {
    await fetchData()
  }, [])

  async function fetchData() {
    setLoading(true)
    let response = await fetch('http://localhost:8080/stories')
    let json = await response.json()
    setLoading(false)
    setStories(json)
  }

  async function postVote(direction, id) {
    let directionData = { direction: direction }

    await fetch(`http://localhost:8080/stories/${id}/votes`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(directionData)
    })
  }

  async function submitBlog(name, message) {
    let blogData = { from: name, message: message }

    await fetch('https://sigma-micro-blogging.herokuapp.com/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(blogData)
    })
  }

  function getStoriesComponentList(stories) {
    return stories.map((story) => <Story key={story.title} story={story} />)
  }

  function getLoadingComponent() {
    return <div className="loader" />
  }

  return (
    <div className="App">
      <header>
        <h1>Very Good™️ Social News Site</h1>
      </header>
      <main>
        <h2>Top Stories</h2>
        {loading ? getLoadingComponent() : getStoriesComponentList(stories)}
      </main>
    </div>
  )
}

export default App
