import './App.css'
import React, { useEffect, useState } from 'react'
import Story from './Story'

function App(props) {
  const [stories, setStories] = useState([])
  const [loading, setLoading] = useState(false)
  const [newsText, setNewsText] = useState('')
  const [url, setUrl] = useState('')

  useEffect(async () => {
    await fetchData()
  }, [])

  async function fetchData() {
    // setLoading(true)
    let response = await fetch('http://localhost:8080/stories')
    let json = await response.json()
    setLoading(false)
    setStories(json)
  }

  async function postVote(direction, id) {
    let directionData = { direction: direction }

    await fetch(`http://localhost:8080/stories/${id}/votes`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(directionData)
    })
    fetchData()
  }

  function getStoriesComponentList(stories) {
    if (stories.length > 0) {
      return stories.map((story) => (
        <Story
          key={story.title}
          story={story}
          postVote={postVote}
          id={story.id}
        />
      ))
    } else {
      return <p>no stories yet</p>
    }
  }

  function getLoadingComponent() {
    return <div className="loader" />
  }

  return (
    <div className="App">
      <header>
        <h1>Very Good™️ Social News Site</h1>
      </header>
      <main>
        <h2>Top Stories</h2>
        {loading ? getLoadingComponent() : getStoriesComponentList(stories)}
      </main>
      <div className="add-news">
        <input
          type="text"
          className="add-news write-news-story"
          value={newsText}
          onChange={(e) => setNewsText(e.target.value)}
        ></input>
        <input
          type="text"
          className="add-news write-url"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        ></input>
        <input
          type="button"
          value="add news story"
          onClick={handleAddNewsStory}
        ></input>
      </div>
    </div>
  )
}

export default App
